package com.amandris.clients.service.dao;import org.apache.ojb.broker.PersistenceBroker;import org.apache.ojb.broker.PersistenceBrokerException;import org.apache.ojb.broker.PersistenceBrokerFactory;import org.apache.ojb.broker.query.Criteria;import org.apache.ojb.broker.query.QueryByCriteria;import com.amandris.clients.service.vo.BuyerHistoricCredit;import com.amandris.clients.util.exception.DataDeleteErrorException;import com.amandris.clients.util.exception.DataUpdateErrorException;public class BuyerHistoricCreditDAO{			public void setBuyerHistoricCredit(BuyerHistoricCredit buyerHistoricCredit)  throws DataUpdateErrorException{		PersistenceBroker broker = null;				try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.store( buyerHistoricCredit);			broker.commitTransaction();		}catch (PersistenceBrokerException ex){			broker.abortTransaction();			throw new DataUpdateErrorException( BuyerHistoricCredit.class.getName(), this.getClass().getName() + ".setBuyerHistoricCredit()", ex);		}finally {			if (broker != null) {				broker.close();			}		}	}		public void deleteBuyerHistoricCreditByBuyerId( Integer buyerId) throws DataDeleteErrorException	{		Criteria 			criteria	= new Criteria();		PersistenceBroker 	broker 		= null;				try {			criteria.addNotEqualTo	( "id", new Integer( 0));			criteria.addEqualTo		( "buyerId", buyerId);									QueryByCriteria query = new QueryByCriteria( BuyerHistoricCredit.class, criteria);									broker = PersistenceBrokerFactory.defaultPersistenceBroker();				broker.deleteByQuery( query);					}catch ( PersistenceBrokerException ex) {			throw new DataDeleteErrorException( BuyerHistoricCredit.class.getName(), this.getClass().getName() + ".deleteBuyerHistoricCreditByBuyerId()", ex);		}finally {			if ( broker != null) {				broker.close();			}		}	}		}