package com.amandris.clients.service.dao;import org.apache.ojb.broker.PersistenceBroker;import org.apache.ojb.broker.PersistenceBrokerException;import org.apache.ojb.broker.PersistenceBrokerFactory;import org.apache.ojb.broker.query.Criteria;import org.apache.ojb.broker.query.QueryByCriteria;import com.amandris.clients.service.vo.SellerHistoricLogin;import com.amandris.clients.util.exception.DataDeleteErrorException;import com.amandris.clients.util.exception.DataUpdateErrorException;public class SellerHistoricLoginDAO{				public void setSellerHistoricLogin( SellerHistoricLogin sellerHistoricLogin)  throws DataUpdateErrorException{		PersistenceBroker broker = null;			try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.store( sellerHistoricLogin);			broker.commitTransaction();		}catch (PersistenceBrokerException ex){			broker.abortTransaction();			throw new DataUpdateErrorException( SellerHistoricLogin.class.getName(), this.getClass().getName() + ".setSellerHistoricLogin()", ex);		}finally {			if (broker != null) {				broker.close();			}		}	}				public void deleteSellerHistoricLoginBySellerId( Integer sellerId) throws DataDeleteErrorException	{		Criteria 			criteria	= new Criteria();		PersistenceBroker 	broker 		= null;				try {			criteria.addNotEqualTo	( "id", new Integer( 0));			criteria.addEqualTo		( "sellerId", sellerId);									QueryByCriteria query = new QueryByCriteria( SellerHistoricLogin.class, criteria);									broker = PersistenceBrokerFactory.defaultPersistenceBroker();				broker.deleteByQuery( query);					}catch ( PersistenceBrokerException ex) {			throw new DataDeleteErrorException( SellerHistoricLogin.class.getName(), this.getClass().getName() + ".deleteSellerHistoricLoginBySellerId()", ex);		}finally {			if ( broker != null) {				broker.close();			}		}	}}