package com.amandris.clients.service.facade;import java.util.Calendar;import java.util.Date;import java.util.Locale;import com.amandris.clients.service.dao.SellerDAO;import com.amandris.clients.service.dao.SellerHistoricLoginDAO;import com.amandris.clients.service.vo.Seller;import com.amandris.clients.service.vo.SellerHistoricLogin;import com.amandris.clients.util.exception.DataAccessErrorException;import com.amandris.clients.util.exception.DataUpdateErrorException;public class SellerHistoricLoginFacade {	public boolean setSellerHistoricLogin( Integer sellerId, String ipAddress, boolean isLogin, Locale locale) throws DataAccessErrorException, DataUpdateErrorException	{		SellerHistoricLoginDAO	sellerHistoricLoginDAO	= new SellerHistoricLoginDAO();		SellerDAO				sellerDAO				= new SellerDAO();		SellerHistoricLogin		sellerHistoricLogin		= new SellerHistoricLogin();		Seller					seller					= new Seller();		Date					today					= Calendar.getInstance( locale).getTime();				seller = sellerDAO.getSellerById( sellerId);			if( seller == null)					return false;				sellerHistoricLogin.setDate		( today);		sellerHistoricLogin.setSeller	( seller);		sellerHistoricLogin.setSellerId	( seller.getId());		sellerHistoricLogin.setIpAddress( ipAddress);		sellerHistoricLogin.setIsLogin	( ( isLogin ? new Integer (1) : new Integer (0)));				sellerHistoricLoginDAO.setSellerHistoricLogin( sellerHistoricLogin);				seller.setLastLogin( today);		seller.setIsOnLine( new Integer( 1));				sellerDAO.setSeller( seller);				return true;	}		}