package com.amandris.clients.service.vo;import java.io.Serializable;import java.util.Date;public class AmandrisMessage implements Serializable{	private Integer 	id;	private Integer 	userId;	private Integer 	userType;	private String 		subject;	private Date		date;	private String 		text;	private String	 	email;	private Integer 	isNew;		private static final long serialVersionUID = 943710884532221862L;	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public Integer getIsNew() {		return isNew;	}	public void setIsNew(Integer isNew) {		this.isNew = isNew;	}	public String getSubject() {		return subject;	}	public void setSubject(String subject) {		this.subject = subject;	}	public String getText() {		return text;	}	public void setText(String text) {		this.text = text;	}	public Integer getUserId() {		return userId;	}	public void setUserId(Integer userId) {		this.userId = userId;	}	public Integer getUserType() {		return userType;	}	public void setUserType(Integer userType) {		this.userType = userType;	}	public Date getDate() {		return date;	}	public void setDate(Date date) {		this.date = date;	}				}