package com.amandris.clients.service.vo;import java.io.Serializable;import java.util.Date;public class BuyerHistoricCredit implements Serializable{	private Integer 		id;	private Integer 		buyerId;	private Integer 		creditActionConst;	private Integer 		credits;	private Integer 		result;	private Date 			date;	private Buyer 			buyer;		private static final long serialVersionUID = 6526471155622776003L;			public Buyer getBuyer() {		return buyer;	}	public void setBuyer(Buyer buyer) {		this.buyer = buyer;	}	public Integer getBuyerId() {		return buyerId;	}	public void setBuyerId(Integer buyerId) {		this.buyerId = buyerId;	}	public Integer getCreditActionConst() {		return creditActionConst;	}	public void setCreditActionConst(Integer creditActionConst) {		this.creditActionConst = creditActionConst;	}	public Integer getCredits() {		return credits;	}	public void setCredits(Integer credits) {		this.credits = credits;	}	public Date getDate() {		return date;	}	public void setDate(Date date) {		this.date = date;	}	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public Integer getResult() {		return result;	}	public void setResult(Integer result) {		this.result = result;	}}