package com.amandris.clients.service.vo;import java.io.Serializable;import java.util.Date;public class BuyerMessage implements Serializable{	private Integer id;	private Integer buyerId;	private String 	sender;	private Integer	active;	private String  subject;	private String  text;	private Integer isHtml;	private Integer isNew;	private Date 	date;	private Buyer 	buyer;		private static final long serialVersionUID = 6526471155622776005L;		public Buyer getBuyer() {		return buyer;	}	public void setBuyer(Buyer buyer) {		this.buyer = buyer;	}	public Integer getBuyerId() {		return buyerId;	}	public void setBuyerId(Integer buyerId) {		this.buyerId = buyerId;	}	public Date getDate() {		return date;	}	public void setDate(Date date) {		this.date = date;	}	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public Integer getIsHtml() {		return isHtml;	}	public void setIsHtml(Integer isHtml) {		this.isHtml = isHtml;	}	public Integer getIsNew() {		return isNew;	}	public void setIsNew(Integer isNew) {		this.isNew = isNew;	}	public String getSender() {		return sender;	}	public void setSender(String sender) {		this.sender = sender;	}	public String getText() {		return text;	}	public void setText(String text) {		this.text = text;	}	public Integer getActive() {		return active;	}	public void setActive(Integer active) {		this.active = active;	}	public String getSubject() {		return subject;	}	public void setSubject(String subject) {		this.subject = subject;	}}