package com.amandris.clients.service.vo;import java.io.Serializable;import java.util.Date;public class BuyerRecomendation implements Serializable{	private Integer id;	private Integer buyerId;	private String  email;	private Date 	date;	private Buyer 	buyer;	private Integer	used;		private static final long serialVersionUID = 6526471155622776006L;		public Buyer getBuyer() {		return buyer;	}	public void setBuyer(Buyer buyer) {		this.buyer = buyer;	}	public Integer getBuyerId() {		return buyerId;	}	public void setBuyerId(Integer buyerId) {		this.buyerId = buyerId;	}	public Date getDate() {		return date;	}	public void setDate(Date date) {		this.date = date;	}	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public Integer getUsed() {		return used;	}	public void setUsed(Integer used) {		this.used = used;	}}