package com.amandris.clients.service.vo;import java.io.Serializable;import java.util.Collection;public class Country implements Serializable{	private Integer	 	id;	private String 		text;	private Collection	states;		private static final long serialVersionUID = 6526471155622776011L;		public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public String getText() {		return text;	}	public void setText(String text) {		this.text = text;	}	public Collection getStates() {		return states;	}	public void setStates(Collection states) {		this.states = states;	}}