package com.amandris.clients.util.exception;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;

public class TranslateValueObjectErrorException extends Exception{	 	public TranslateValueObjectErrorException( String valueObject, String viewObject, Exception e)	{		super( "\nNo se ha podido realizar la transformación del objeto " + valueObject + " en " + viewObject + "\n");		Log log = LogFactory.getLog( this.getClass());		if ( log.isErrorEnabled()){			log.error("\nNo se ha podido realizar la transformación del objeto " + valueObject + " en " + viewObject + "\n" + e.toString());		}		System.out.println("\nNo se ha podido realizar la transformación del objeto " + valueObject + " en " + viewObject + "\n" + e.toString());							}}
