package com.amandris.clients.util.translator;import com.amandris.clients.service.vo.AlbumElement;import com.amandris.clients.util.DBUtils;import com.amandris.clients.util.exception.TranslateValueObjectErrorException;import com.amandris.clients.web.view.AlbumElementView;public class AlbumElementToAlbumElementViewTranslator extends Translator {	public Object translateView() throws TranslateValueObjectErrorException	{		AlbumElementView 	albumElementView 	= new AlbumElementView();		AlbumElement		albumElement		= ( AlbumElement) object;				try{			albumElementView.setId					( DBUtils.controlNull( albumElement.getId()));			albumElementView.setActive				( DBUtils.controlNull( albumElement.getActive()));			albumElementView.setCode				( DBUtils.controlNull( albumElement.getCode()));			albumElementView.setMediaTypeConst		( DBUtils.controlNull( albumElement.getMediaTypeConst()));			albumElementView.setSellerId			( DBUtils.controlNull( albumElement.getSellerId()));			albumElementView.setText				( DBUtils.controlNull( albumElement.getText()));			albumElementView.setViews				( DBUtils.controlNull( albumElement.getViews()));			albumElementView.setVotes				( DBUtils.controlNull( albumElement.getVotes()));		}catch( Exception e) {			throw new TranslateValueObjectErrorException( object.getClass().getName(), albumElementView.getClass().getName(), e);		}				return albumElementView;	}}