package com.amandris.clients.util.translator;import com.amandris.clients.service.vo.AmandrisMessage;import com.amandris.clients.util.DBUtils;import com.amandris.clients.util.exception.TranslateValueObjectErrorException;import com.amandris.clients.web.view.AmandrisMessageView;public class AmandrisMessageToAmandrisMessageViewTranslator extends Translator {	public Object translateView() throws TranslateValueObjectErrorException	{		AmandrisMessageView 	amandrisMessageView 		= new AmandrisMessageView();		AmandrisMessage			amandrisMessage				= ( AmandrisMessage) object;										try{			amandrisMessageView.setId			( DBUtils.controlNull( amandrisMessage.getId()));			amandrisMessageView.setDate			( DBUtils.getDate( amandrisMessage.getDate(), 4));			amandrisMessageView.setEmail		( DBUtils.controlNull( amandrisMessage.getEmail()));			amandrisMessageView.setIsNew		( DBUtils.controlNull( amandrisMessage.getIsNew()));			amandrisMessageView.setSubject		( DBUtils.controlNull( amandrisMessage.getSubject()));			amandrisMessageView.setBriefSubject	( DBUtils.briefString( amandrisMessage.getSubject(), 20));			amandrisMessageView.setText			( DBUtils.controlNull( amandrisMessage.getText()));			amandrisMessageView.setUserId		( DBUtils.controlNull( amandrisMessage.getUserId()));			amandrisMessageView.setUserType		( DBUtils.controlNull( amandrisMessage.getUserType()));		}catch( Exception e) {			e.printStackTrace();			throw new TranslateValueObjectErrorException( object.getClass().getName(), amandrisMessageView.getClass().getName(), e);		}		return amandrisMessageView;	}}