package com.amandris.clients.util.translator;import com.amandris.clients.service.vo.BuyerMessage;import com.amandris.clients.util.DBUtils;import com.amandris.clients.util.exception.TranslateValueObjectErrorException;import com.amandris.clients.web.view.MessageView;public class BuyerMessageToMessageViewTranslator extends Translator {	public Object translateView() throws TranslateValueObjectErrorException	{		MessageView 	messageView 	= new MessageView();		BuyerMessage	buyerMessage	= ( BuyerMessage) object;				try{			messageView.setId					( DBUtils.controlNull( buyerMessage.getId()));			messageView.setActive				( DBUtils.controlNull( buyerMessage.getActive()));			messageView.setBuyerId				( DBUtils.controlNull( buyerMessage.getBuyerId()));			messageView.setDate					( DBUtils.getDate( buyerMessage.getDate(), 3));			messageView.setIsNew				( DBUtils.controlNull( buyerMessage.getIsNew()));			messageView.setIsHtml				( DBUtils.controlNull( buyerMessage.getIsHtml()));			messageView.setSender				( DBUtils.controlNull( buyerMessage.getSender()));			messageView.setBriefSubject			( DBUtils.briefString( buyerMessage.getSubject(), 30));			messageView.setSubject				( DBUtils.controlNull( buyerMessage.getSubject()));			messageView.setText					( DBUtils.controlNull( buyerMessage.getText()));		}catch( Exception e) {			throw new TranslateValueObjectErrorException( object.getClass().getName(), messageView.getClass().getName(), e);		}				return messageView;	}}