package com.amandris.clients.util.translator;import com.amandris.clients.service.vo.ContactMessage;import com.amandris.clients.util.DBUtils;import com.amandris.clients.util.exception.TranslateValueObjectErrorException;import com.amandris.clients.web.view.ContactMessageView;public class ContactMessageToContactMessageViewTranslator extends Translator {	public Object translateView() throws TranslateValueObjectErrorException	{		ContactMessageView 	contactMessageView 	= new ContactMessageView();		ContactMessage		contactMessage		= ( ContactMessage) object;		try{			contactMessageView.setId				( DBUtils.controlNull( contactMessage.getId()));			contactMessageView.setContactId			( DBUtils.controlNull( contactMessage.getContactId()));			contactMessageView.setDate				( DBUtils.getDate( contactMessage.getDate(), 4));			contactMessageView.setIsNew				( DBUtils.controlNull( contactMessage.getIsNew()));			contactMessageView.setSentFromBuyer		( DBUtils.controlNull( contactMessage.getSentFromBuyer()));			contactMessageView.setText				( DBUtils.controlNull( contactMessage.getText()));					}catch( Exception e) {			e.printStackTrace();			throw new TranslateValueObjectErrorException( object.getClass().getName(), contactMessageView.getClass().getName(), e);		}				return contactMessageView;	}}