package com.amandris.clients.util.translator;import com.amandris.clients.service.vo.Country;import com.amandris.clients.util.ConfigurationParameterManager;import com.amandris.clients.util.exception.TranslateValueObjectErrorException;import com.amandris.clients.web.view.CountryView;public class CountryToCountryViewTranslator extends Translator {	public Object translateView() throws TranslateValueObjectErrorException	{		CountryView idTextView 	= new CountryView();		Country		country	= ( Country) object;				try{			idTextView.setId	( country.getId().toString());			idTextView.setText	( ConfigurationParameterManager.getMessages().getMessage( locale, "country." + country.getText()));		}catch( Exception e) {			e.printStackTrace();			throw new TranslateValueObjectErrorException( object.getClass().getName(), idTextView.getClass().getName(), e);		}				return idTextView;	}}