package com.amandris.clients.util.translator;import java.util.Date;import com.amandris.clients.service.vo.Seller;import com.amandris.clients.util.ConfigurationParameterManager;import com.amandris.clients.util.DBUtils;import com.amandris.clients.util.exception.TranslateValueObjectErrorException;import com.amandris.clients.web.view.SellerViewSmall;public class SellerToSellerViewSmallTranslator extends Translator {	public Object translateView() throws TranslateValueObjectErrorException	{		Date medir = new Date();		long medirLong = medir.getTime();				SellerViewSmall 	sellerViewSmall 		= new SellerViewSmall();		Seller		seller			= ( Seller) object;		int			pictureCount	= 0;		String		language		= "";				try{			if( seller.getTextActive().intValue() == 1) {				sellerViewSmall.setCity						( DBUtils.controlNull( seller.getCity()));				sellerViewSmall.setBriefCity				( DBUtils.briefString( seller.getCity(), 14));			} else {				sellerViewSmall.setCity						( ConfigurationParameterManager.getMessages().getMessage( locale, "common.waitingForRevision"));				sellerViewSmall.setBriefCity				( ConfigurationParameterManager.getMessages().getMessage( locale, "common.briefWaitingForRevision"));							}				sellerViewSmall.setHasPicture				( DBUtils.controlNull( seller.getHasPicture()));			sellerViewSmall.setId						( DBUtils.controlNull( seller.getId()));			sellerViewSmall.setAge						( Integer.toString( DBUtils.getAge( seller.getBirthdate(), locale)));			sellerViewSmall.setCountry					( ConfigurationParameterManager.getMessages().getMessage( locale, "country." + DBUtils.controlNull( seller.getCountry().getText())));			sellerViewSmall.setCountryId				( seller.getCountryId());			sellerViewSmall.setLogin					( DBUtils.controlNull( seller.getLogin()));			sellerViewSmall.setBriefLogin				( DBUtils.briefString( seller.getLogin(), 15));						sellerViewSmall.setMainPicture				( DBUtils.controlNull( seller.getMainPicture()));			sellerViewSmall.setMainPictureCode			( DBUtils.controlNull( seller.getMainPictureCode()));			sellerViewSmall.setSex						( ConfigurationParameterManager.getMessages().getMessage( locale, "sex." + DBUtils.controlNull( seller.getSexConst())));			sellerViewSmall.setSexId					( DBUtils.controlNull( seller.getSexConst()));			sellerViewSmall.setState					( ConfigurationParameterManager.getMessages().getMessage( locale, "state." + DBUtils.controlNull( seller.getState().getText())));			sellerViewSmall.setStateId					( seller.getStateId());								}catch( Exception e) {			e.printStackTrace();			throw new TranslateValueObjectErrorException( object.getClass().getName(), sellerViewSmall.getClass().getName(), e);		}				return sellerViewSmall;	}}