package com.amandris.clients.util.translator;import com.amandris.clients.service.vo.State;import com.amandris.clients.util.ConfigurationParameterManager;import com.amandris.clients.util.DBUtils;import com.amandris.clients.util.exception.TranslateValueObjectErrorException;import com.amandris.clients.web.view.StateView;public class StateToStateViewTranslator extends Translator {	public Object translateView() throws TranslateValueObjectErrorException	{		StateView 		stateView 	= new StateView();		State			state		= ( State) object;				try{			stateView.setId			( state.getId().toString());			stateView.setText		( ConfigurationParameterManager.getMessages().getMessage( locale, "state." + state.getText()));			stateView.setCountryId	( DBUtils.controlNull( state.getCountryId()));			stateView.setCountryText( DBUtils.controlNull( state.getCountry().getText()));			stateView.setName		( "state_" + state.getText());		}catch( Exception e) {			throw new TranslateValueObjectErrorException( object.getClass().getName(), stateView.getClass().getName(), e);		}				return stateView;	}}