package com.amandris.clients.util.translator;import com.amandris.clients.service.vo.VoteToSeller;import com.amandris.clients.util.DBUtils;import com.amandris.clients.util.exception.TranslateValueObjectErrorException;import com.amandris.clients.web.view.VoteView;public class VoteToSellerToVoteViewTranslator extends Translator {	public Object translateView() throws TranslateValueObjectErrorException	{		VoteView 		voteView 		= new VoteView();		VoteToSeller	voteToSeller	= ( VoteToSeller) object;				try{			voteView.setId					( voteToSeller.getId().toString());			voteView.setActive				( DBUtils.controlNull( voteToSeller.getActive()));			voteView.setBriefBuyerLogin		( DBUtils.briefString( voteToSeller.getBuyer().getLogin(), 18));			voteView.setBriefComment		( DBUtils.briefString( voteToSeller.getComment(), 30));			voteView.setBriefReply			( DBUtils.briefString( voteToSeller.getReply(), 30));			voteView.setBriefSellerLogin	( DBUtils.briefString( voteToSeller.getSeller().getLogin(), 18));			voteView.setBuyerId				( DBUtils.controlNull( voteToSeller.getBuyerId()));			voteView.setBuyerIsPublic		( DBUtils.controlNull( voteToSeller.getBuyer().getIsPublic()));			voteView.setBuyerHasPicture		( DBUtils.controlNull( voteToSeller.getBuyer().getHasPicture()));			voteView.setBuyerMainPicture	( DBUtils.controlNull( voteToSeller.getBuyer().getMainPicture()));			voteView.setBuyerMainPictureCode( DBUtils.controlNull( voteToSeller.getBuyer().getMainPictureCode()));			voteView.setBuyerLogin			( DBUtils.controlNull( voteToSeller.getBuyer().getLogin()));			voteView.setComment				( DBUtils.controlNull( voteToSeller.getComment()));			voteView.setContactId			( DBUtils.controlNull( voteToSeller.getContactId()));			voteView.setDate				( DBUtils.getDate( voteToSeller.getDate(), 2));			voteView.setTime				( DBUtils.getTime( voteToSeller.getDate()));			voteView.setReply				( DBUtils.controlNull( voteToSeller.getReply()));			voteView.setReplyActive			( DBUtils.controlNull( voteToSeller.getReplyActive()));			voteView.setSellerId			( DBUtils.controlNull( voteToSeller.getSellerId()));			voteView.setSellerLogin			( DBUtils.controlNull( voteToSeller.getSeller().getLogin()));			voteView.setSellerHasPicture	( DBUtils.controlNull( voteToSeller.getSeller().getHasPicture()));			voteView.setSellerMainPicture	( DBUtils.controlNull( voteToSeller.getSeller().getMainPicture()));			voteView.setSellerMainPictureCode( DBUtils.controlNull( voteToSeller.getSeller().getMainPictureCode()));			voteView.setVote				( DBUtils.controlNull( voteToSeller.getVote()));			voteView.setSellerSex			( DBUtils.controlNull( voteToSeller.getSeller().getSexConst()));			voteView.setBuyerSex			( DBUtils.controlNull( voteToSeller.getBuyer().getSexConst()));											}catch( Exception e) {			throw new TranslateValueObjectErrorException( object.getClass().getName(), voteView.getClass().getName(), e);		}				return voteView;	}}