package com.amandris.clients.web.form;import java.io.Serializable;import org.apache.struts.validator.ValidatorForm;public class AdminSendMessageForm extends ValidatorForm implements Serializable{	private String ids;	private String subject;	private String text;		private static final long serialVersionUID = 1418032641456214L;	public String getIds() {		return ids;	}	public void setIds(String ids) {		this.ids = ids;	}	public String getSubject() {		return subject;	}	public void setSubject(String subject) {		this.subject = subject;	}	public String getText() {		return text;	}	public void setText(String text) {		this.text = text;	}	}