package com.amandris.clients.web.util;import java.util.ArrayList;import java.util.Collection;import java.util.Locale;import org.apache.struts.action.ActionForm;public class CountryState extends ActionForm{	private Collection 		country;	private Integer			selectedCountry;	private Integer			selectedState;	private Locale			locale;		private static final long serialVersionUID = 2526471155622776002L;		public CountryState()	{		country = new ArrayList();	}		public Locale getLocale() {		return locale;	}	public void setLocale(Locale locale) {		this.locale = locale;	}	public Collection getCountry() {		return country;	}	public void setCountry(Collection country) {		this.country = country;	}	public Integer getSelectedCountry() {		return selectedCountry;	}	public void setSelectedCountry(Integer selectedCountry) {		this.selectedCountry = selectedCountry;	}	public Integer getSelectedState() {		return selectedState;	}	public void setSelectedState(Integer selectedState) {		this.selectedState = selectedState;	}	}