package com.amandris.clients.web.view;import org.apache.struts.action.ActionForm;public class AmandrisMessageView extends ActionForm{	private String id;	private String userId;	private String userType;	private String date;	private String email;	private String subject;	private String briefSubject;	private String text;	private String isNew;		private static final long serialVersionUID = 5455427755459977714L;	public String getDate() {		return date;	}	public void setDate(String date) {		this.date = date;	}	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getIsNew() {		return isNew;	}	public void setIsNew(String isNew) {		this.isNew = isNew;	}	public String getSubject() {		return subject;	}	public void setSubject(String subject) {		this.subject = subject;	}	public String getText() {		return text;	}	public void setText(String text) {		this.text = text;	}	public String getUserId() {		return userId;	}	public void setUserId(String userId) {		this.userId = userId;	}	public String getUserType() {		return userType;	}	public void setUserType(String userType) {		this.userType = userType;	}	public String getBriefSubject() {		return briefSubject;	}	public void setBriefSubject(String briefSubject) {		this.briefSubject = briefSubject;	}	}