package com.amandris.clients.web.view;import java.util.ArrayList;import java.util.Collection;import org.apache.struts.action.ActionForm;public class CountryView extends ActionForm{	private String 		id;	private String 		text;	private Collection	states;		private static final long serialVersionUID = 1526471155622776005L;		public CountryView()	{		id 		= "";		text 	= "";		states	= new ArrayList();	}	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getText() {		return text;	}	public void setText(String text) {		this.text = text;	}	public Collection getStates() {		return states;	}	public void setStates(Collection states) {		this.states = states;	}			}