package com.amandris.clients.web.view;import org.apache.struts.action.ActionForm;public class IdTextView extends ActionForm{	private String 	id;	private String 	text;		private static final long serialVersionUID = 1526471155622776006L;		public IdTextView()	{		id 			= "";		text 		= "";	}	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getText() {		return text;	}	public void setText(String text) {		this.text = text;	}}