package com.amandris.clients.web.view;import java.util.ArrayList;import org.apache.struts.action.ActionForm;public class InvitationDataView extends ActionForm{	private String 		totalInvitations;	private ArrayList	invitations;		private static final long serialVersionUID = 1526471155622776007L;		public InvitationDataView()	{		totalInvitations		= "";		invitations				= new ArrayList(); 	}	public ArrayList getInvitations() {		return invitations;	}	public void setInvitations(ArrayList invitations) {		this.invitations = invitations;	}	public String getTotalInvitations() {		return totalInvitations;	}	public void setTotalInvitations(String totalInvitations) {		this.totalInvitations = totalInvitations;	}}