package com.amandris.clients.web.view;import java.util.ArrayList;import org.apache.struts.action.ActionForm;public class MessageDataView extends ActionForm{	private String 		newMessages;	private String 		readMessages;	private String 		totalMessages;	private ArrayList	messages;		private static final long serialVersionUID = 1526471155622776009L;		public MessageDataView()	{		newMessages				= "";		readMessages			= "";		totalMessages			= "";		messages				= new ArrayList(); 	}	public ArrayList getMessages() {		return messages;	}	public void setMessages(ArrayList messages) {		this.messages = messages;	}	public String getNewMessages() {		return newMessages;	}	public void setNewMessages(String newMessages) {		this.newMessages = newMessages;	}	public String getReadMessages() {		return readMessages;	}	public void setReadMessages(String readMessages) {		this.readMessages = readMessages;	}	public String getTotalMessages() {		return totalMessages;	}	public void setTotalMessages(String totalMessages) {		this.totalMessages = totalMessages;	}	}