package com.amandris.clients.web.view;import java.util.ArrayList;import org.apache.struts.action.ActionForm;public class PromotionDataView extends ActionForm{	private String 		promotionCount;	private ArrayList	promotions;		private static final long serialVersionUID = 2571053751878839802L;		public PromotionDataView()	{		promotionCount			= "";		promotions				= new ArrayList(); 	}	public String getPromotionCount() {		return promotionCount;	}	public void setPromotionCount(String promotionCount) {		this.promotionCount = promotionCount;	}	public ArrayList getPromotions() {		return promotions;	}	public void setPromotions(ArrayList promotions) {		this.promotions = promotions;	}}