package com.amandris.clients.web.view;import java.util.Date;import org.apache.struts.action.ActionForm;public class PromotionView extends ActionForm{	private String 	id;	private String 	sellerId;	private String 	promotionConst;	private String  startDate;	private String  endDate;	private Date  	startJavaDate;	private Date  	endJavaDate;	private String  oddOrEven;		private static final long serialVersionUID = 1526471155622776010L;		public PromotionView()	{		id					= "";		sellerId			= "";		promotionConst		= "";		startDate			= "";		endDate				= "";		oddOrEven			= "";	}	public String getEndDate() {		return endDate;	}	public void setEndDate(String endDate) {		this.endDate = endDate;	}	public Date getEndJavaDate() {		return endJavaDate;	}	public void setEndJavaDate(Date endJavaDate) {		this.endJavaDate = endJavaDate;	}	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getOddOrEven() {		return oddOrEven;	}	public void setOddOrEven(String oddOrEven) {		this.oddOrEven = oddOrEven;	}	public String getPromotionConst() {		return promotionConst;	}	public void setPromotionConst(String promotionConst) {		this.promotionConst = promotionConst;	}	public String getSellerId() {		return sellerId;	}	public void setSellerId(String sellerId) {		this.sellerId = sellerId;	}	public String getStartDate() {		return startDate;	}	public void setStartDate(String startDate) {		this.startDate = startDate;	}	public Date getStartJavaDate() {		return startJavaDate;	}	public void setStartJavaDate(Date startJavaDate) {		this.startJavaDate = startJavaDate;	}				}